# while let Some(value) = rx.recv().await {
# println!("received '{value}'");
# } }); let tx_fut = pin!(async move { // --snip--
# let vals = vec![
# String::from("more"),
# String::from("messages"),
# String::from("for"),
# String::from("you"),
# ];
# # for val in vals {
# tx.send(val).unwrap();
