enum ShirtColor { Red, Blue,
} struct Inventory { shirts: Vec<ShirtColor>,
} impl Inventory { fn giveaway(&self, user_preference: Option<ShirtColor>) -> ShirtColor { user_preference.unwrap_or_else(|| self.most_stocked()) } fn most_stocked(&self) -> ShirtColor { let mut num_red = 0; let mut num_blue = 0; for color in &self.shirts { match color { ShirtColor::Red => num_red += 1, ShirtColor::Blue => num_blue += 1, } } if num_red > num_blue { ShirtColor::Red } else { ShirtColor::Blue } }
} fn main() { let store = Inventory { shirts: vec![ShirtColor::Blue, ShirtColor::Red, ShirtColor::Blue], }; let user_pref1 = Some(ShirtColor::Red); let giveaway1 = store.giveaway(user_pref1); println!( "The user with preference {:?} gets {:?}", user_pref1, giveaway1 ); let user_pref2 = None; let giveaway2 = store.giveaway(user_pref2); println!( "The user with preference {:?} gets {:?}", user_pref2, giveaway2 );
} Listing 13-1: T æ¤å…¬å¸èµ é€åœºæ™¯ åœ¨ main ä¸­å®šä¹‰çš„ store æœ‰ä¸¤ä»¶è“è‰² T æ¤å’Œä¸€ä»¶çº¢è‰² T æ¤å‰©ä½™ï¼Œç”¨äºæ­¤æ¬¡é™é‡ç‰ˆä¿ƒé”€æ´»åŠ¨ã€‚æˆ‘ä»¬ä¸ºæœ‰çº¢è‰² T æ¤åå¥½çš„ç”¨æˆ·å’Œæ²¡æœ‰ä»»ä½•åå¥½çš„ç”¨æˆ·è°ƒç”¨ giveaway æ–¹æ³•ã€‚ å†æ¬¡å¼ºè°ƒï¼Œè¿™æ®µä»£ç å¯ä»¥é€šè¿‡å¤šç§æ–¹å¼å®ç°ï¼Œåœ¨è¿™é‡Œï¼Œä¸ºäº†ä¸“æ³¨äºé—­åŒ…ï¼Œæˆ‘ä»¬åšæŒä½¿ç”¨ä½ å·²ç»å­¦è¿‡çš„æ¦‚å¿µï¼Œé™¤äº† giveaway æ–¹æ³•çš„ä¸»ä½“ä½¿ç”¨äº†é—­åŒ…ã€‚åœ¨ giveaway æ–¹æ³•ä¸­ï¼Œæˆ‘ä»¬è·å–ç”¨æˆ·åå¥½ä½œä¸º Option<ShirtColor> ç±»å‹çš„å‚æ•°ï¼Œå¹¶åœ¨ user_preference ä¸Šè°ƒç”¨ unwrap_or_else æ–¹æ³•ã€‚æ ‡å‡†åº“å®šä¹‰äº† Option<T> ä¸Šçš„ unwrap_or_else æ–¹æ³• ã€‚å®ƒæ¥å—ä¸€ä¸ªå‚æ•°ï¼šä¸€ä¸ªä¸å¸¦ä»»ä½•å‚æ•°çš„é—­åŒ…ï¼Œè¿”å›ä¸€ä¸ªå€¼ Tï¼ˆä¸ Option<T> çš„ Some å˜ä½“ä¸­å­˜å‚¨çš„ç±»å‹ç›¸åŒï¼Œåœ¨è¿™ç§æƒ…å†µä¸‹æ˜¯ ShirtColorï¼‰ã€‚å¦‚æœ Option<T> æ˜¯ Some å˜ä½“ï¼Œunwrap_or_else è¿”å› Some ä¸­çš„å€¼ã€‚å¦‚æœ Option<T> æ˜¯ None å˜ä½“ï¼Œunwrap_or_else è°ƒç”¨é—­åŒ…å¹¶è¿”å›é—­åŒ…è¿”å›çš„å€¼ã€‚ æˆ‘ä»¬å°†é—­åŒ…è¡¨è¾¾å¼ || self.most_stocked() æŒ‡å®šä¸º unwrap_or_else çš„å‚æ•°ã€‚è¿™æ˜¯ä¸€ä¸ªä¸å¸¦å‚æ•°çš„é—­åŒ…ï¼ˆå¦‚æœé—­åŒ…æœ‰å‚æ•°ï¼Œå®ƒä»¬å°†å‡ºç°åœ¨ä¸¤ä¸ªç«–çº¿ä¹‹é—´ï¼‰ã€‚é—­åŒ…çš„ä¸»ä½“è°ƒç”¨ self.most_stocked()ã€‚æˆ‘ä»¬åœ¨è¿™é‡Œå®šä¹‰é—­åŒ…ï¼Œunwrap_or_else çš„å®ç°å°†åœ¨éœ€è¦ç»“æœæ—¶è¯„ä¼°é—­åŒ…ã€‚ è¿è¡Œæ­¤ä»£ç ä¼šæ‰“å°ï¼š $ cargo run Compiling shirt-company v0.1.0 (file:///projects/shirt-company) Finished `dev` profile [unoptimized + debuginfo] target(s) in 0.27s Running `target/debug/shirt-company`
